<template>
  <!-- 首页 -->
  <div class="app-box">
    <a-row :gutter="[16, 0]" class="account">
      <a-col :span="7">
        <!-- 用户信息卡片 -->
        <a-card class="account-center">
          <!-- 用户信息 - 头部 -->
          <div class="account-center-avatarHolder">
            <div class="avatar">
              <a-image
                src="https://www.antdv.com/#error"
                fallback="data:image/png;base64,"
              />
            </div>
            <div class="username">webdyc</div>
            <div class="bio">求知若愚，求贤若饥</div>
          </div>
          <!-- 用户信息 - 详情 -->
          <div class="account-center-detail">
            <p><radar-chart-outlined />web前端</p>
            <p><cluster-outlined />技术部</p>
            <p><qq-outlined />123456</p>
          </div>
          <!-- 分割线 -->
          <a-divider />
          <!-- 标签 -->
          <div class="account-center-tags">
            <div class="tagsTitle">标签</div>
            <div class="mt-1">
              <template v-for="(tag, index) in tags" :key="tag">
                <a-tooltip v-if="tag.length > 20" :title="tag">
                  <a-tag :closable="index >= 5" @close="handleClose(tag)">
                    {{ `${tag.slice(0, 20)}...` }}
                  </a-tag>
                </a-tooltip>
                <a-tag v-else :closable="index >= 5" @close="handleClose(tag)">
                  {{ tag }}
                </a-tag>
              </template>
              <a-input
                v-if="inputVisible"
                ref="inputRef"
                v-model:value="inputValue"
                type="text"
                size="small"
                :style="{ width: '78px' }"
                @blur="handleInputConfirm"
                @keyup.enter="handleInputConfirm"
              />
              <a-tag
                v-else
                style="background: #fff; border-style: dashed"
                @click="showInput"
              >
                <plus-outlined />
                New Tag
              </a-tag>
            </div>
          </div>
        </a-card>
      </a-col>
      <a-col :span="17">
        <!-- 用户信息卡片 -->
        <a-card class="tabs-center">
          <a-tabs v-model:activeKey="activeKey">
            <a-tab-pane key="1" tab="文章"> Content of Tab Pane 1 </a-tab-pane>
            <a-tab-pane key="2" tab="内容" force-render>
              Content of Tab Pane 2
            </a-tab-pane>
            <a-tab-pane key="3" tab="项目"> Content of Tab Pane 3 </a-tab-pane>
          </a-tabs>
        </a-card>
      </a-col>
    </a-row>
  </div>
</template>
<script>
import {
  RadarChartOutlined,
  ClusterOutlined,
  QqOutlined
} from '@ant-design/icons-vue'
import { ref, reactive, toRefs, nextTick } from 'vue'
import { PlusOutlined } from '@ant-design/icons-vue'

export default {
  name: 'Home',
  components: {
    RadarChartOutlined,
    ClusterOutlined,
    QqOutlined,
    PlusOutlined
  },
  setup() {
    const inputRef = ref()
    const state = reactive({
      activeKey: '1',
      tags: ['HTML', 'CSS', 'JavaScript', 'Vue', 'React'],
      inputVisible: false,
      inputValue: ''
    })

    const handleClose = (removedTag) => {
      const tags = state.tags.filter((tag) => tag !== removedTag)
      console.log(tags)
      state.tags = tags
    }

    const showInput = () => {
      state.inputVisible = true
      nextTick(() => {
        inputRef.value.focus()
      })
    }

    const handleInputConfirm = () => {
      const inputValue = state.inputValue
      let tags = state.tags

      if (inputValue && tags.indexOf(inputValue) === -1) {
        tags = [...tags, inputValue]
      }
      Object.assign(state, {
        tags,
        inputVisible: false,
        inputValue: ''
      })
    }

    return {
      ...toRefs(state),
      handleClose,
      showInput,
      handleInputConfirm,
      inputRef
    }
  }
}
</script>
